mapper.insert:
insert into Card_receive (
	alipay_user_id, cert_no, real_name,gender, archive_number, type
)
values(
	#{alipayUserId}, #{certNo}, #{realName}, #{gender}, #{archiveNumber}, #{type}
)

mapper.update:
UPDATE
	Card_receive 
SET 
	type = #{deleteType}
WHERE 1 = 1
	AND alipay_user_id = #{alipayUserId}
	AND cert_no = #{certNo}
	AND type = #{keepType}
